mapper.update:
update goods 

	GOODS_PRICE = #{goodsPrice}, 
	GOODS_CONTENTS = #{goodsContents}, VISIT_COUNT = #{visitCount}, 
	GOODS_UPDATE_DATE = sysdate, EMP_NUM = #{empNum}, 
	GOODS_REGIST = #{goodsRegist}, 
	UPDATE_EMP_NUM = #{updateEmpNum}, GOODS_NAME = #{goodsName}
	, goods_main_image = #{goodsMainImage}, goods_main_store_image = #{goodsMainStoreImage}
	, goods_detail_image = #{goodsDetailImage}, goods_detail_store_image = #{goodsDetailStoreImage}

	where goods_num = #{goodsNum}
